SELECT small_community_id,area_code, name, community_id,status,create_time, update_time,deliverer_id FROM small_community
status="NORMAL"
AND area_code in
${item}
AND community_id in
${item1}
AND name LIKE concat('%',#{name},'%')
AND community_id = #{communityId}
AND IFNULL(community_id,'')!= -#{communityId}
AND community_id is not null
AND community_id is null
ORDER BY small_community_id DESC

