mapper.insert:
insert into community (community_id, name, mobile, 
  qq, email, address, 
  status, area_code,create_time, update_time
  )
values (#{communityId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{mobile,jdbcType=VARCHAR}, 
  #{qq,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR}, 
  #{status,jdbcType=VARCHAR},#{areaCode,jdbcType=VARCHAR},#{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT community_id AS communityId,mobile, name,qq, address,email,status,area_code,create_time AS createTime, update_time AS updateTime FROM community
status="NORMAL"
AND community_id=#{communityId}
AND mobile=#{mobile}
AND address LIKE concat('%',#{address},'%')
AND email=#{email}
AND name LIKE concat('%',#{name},'%')
ORDER BY community_id DESC

SELECT community_id,mobile, name,qq, address,email,status,area_code,create_time, update_time FROM community
status="NORMAL"
AND community_id=#{communityId}
AND mobile=#{mobile}
AND address LIKE concat('%',#{address},'%')
AND email=#{email}
AND name LIKE concat('%',#{name},'%')
AND qq = #{qq}
