mapper.select:
select

from small_community_req
where 1=1

  and small_community_req_id=#{smallCommunityReqId,jdbcType=INTEGER}


  and small_community LIKE concat('%',#{smallCommunity,jdbcType=VARCHAR},'%')


  and `status`=#{status,jdbcType=VARCHAR}


  and mobile like concat('%',#{mobile,jdbcType=VARCHAR},'%')


  and nickname=#{nickname,jdbcType=VARCHAR}



  and create_time   >=  #{startTime,jdbcType=TIMESTAMP}


  and create_time  <=  #{endTime} ,jdbcType=TIMESTAMP}

 order by create_time desc
