mapper.select:
select community_goods_id, community_id, goods_sku_id, min_supermarket_id, min_price,max_supermarket_id, max_price, create_time, update_time from community_goods


    community_goods_id = #{communityGoodsId}


    and community_id = #{communityId}


    and goods_sku_id = #{goodsSkuId}


    and min_supermarket_id = #{minSupermarketId}


    and min_price = #{minPrice}


    and max_supermarket_id = #{maxSupermarketId}


    and max_price = #{maxPrice}

mapper.delete:
DELETE FROM
			community_goods
	  WHERE
			community_id=#{communityId}	

	 	AND	goods_sku_id=#{goodsSkuId}
