mapper.update:
update
	open_member_info
set
	start_time =#{startTime},end_time=#{endTime}
where
	open_member_info_id=#{openMemberInfoId}

mapper.select:
select  from open_member_info


        open_member_info_id = #{openMemberInfoId}


        AND user_id = #{userId}


        AND member_level = #{memberLevel}


        AND start_time = #{startTime}


        AND end_time  >=  #{endTime}


        AND order_man_id = #{orderManId}


ORDER BY create_time DESC
